            _tokenApprovals[uint16(tokenId)] == msg.sender ||
                owner == msg.sender,
            "ERC721: caller is not the owner nor an approved operator for the token"
        );
        _tokenApprovals[uint16(tokenId)] = to;
        emit Approval(owner, to, tokenId);
    }
}

